function toggleSort() {
    return sort.hidden ? sort.hidden = !1 : sort.hidden = !0, !1;
}

function toggleFilter() {
    for (var a = document.getElementById("sortType"), b = document.getElementById("sortOrder"), c = 0; c < radioBtn.length; c++) radioBtn[c].checked && c < 2 ? a.innerText = radioBtn[c].value : radioBtn[c].checked && c > 1 && (b.innerText = radioBtn[c].value);
}

if (document.getElementById("exercises"), document.getElementById("list")) {
    var filter = document.getElementById("chordList").getElementsByTagName("nav"), sort = document.getElementById("sortOverlay"), radioBtn = sort.getElementsByTagName("input");
    sort.hidden = !0, filter[0].addEventListener("click", toggleSort, !1);
    for (var i = 0; i < radioBtn.length; i++) radioBtn[i].addEventListener("click", toggleFilter, !0);
}